const fixedHeaderWhenScrolling=()=>{const e=document.querySelector(".header");window.scrollY>0?e.classList.add("header--fixed"):e.classList.remove("header--fixed")},resizeBlockHeight=(e,t)=>{const d=document.querySelectorAll(e),n=document.querySelector(t),o=window.getComputedStyle(n).height;d.forEach((e=>{e.style.height=o}))};document.addEventListener("DOMContentLoaded",(()=>{resizeBlockHeight(".advantages__img",".advantages__info"),window.addEventListener("scroll",(function(){fixedHeaderWhenScrolling()})),window.onresize=()=>{resizeBlockHeight(".advantages__img",".advantages__info")}}));